```cpp
#include <cstdio>
#include <cstring>
#include <iostream>
using namespace std;
const int N = 1100;
int n, p, s, w[N], v[N], l = N, r, mid, ans = -1, f[N];
bool check(int x) {
	memset (f, 0, sizeof (f));
	for(int i = 1; i <= n; i++) 
		if (w[i] <= x)
			for(int j = s; j >= w[i]; j--)
				f[j] = max(f[j], f[j - w[i]] + v[i]);
	if (f[s] < p) return false;
	return true;
}
int main() {
	scanf("%d%d%d", &n, &p, &s);
	for(int i = 1; i <= n; i++) {
		scanf("%d%d", &w[i], &v[i]);
		if(w[i] > r) r = w[i];
		if(w[i] < l) l = w[i];
	}
	while(l < r) {
		mid = (l + r) >> 1;
		if(check(mid)) {
			ans = mid;
			r = mid;
		}
		else l = mid + 1;
	}
	if(ans != -1) 
		printf("%d\n", ans);
	else printf ("No Solution!\n");
	return 0;
}
