其实此题只需要二分套背包  
注释见代码:  
```cpp
#include<bits/stdc++.h>
using namespace std;
long long n,V,S,s[1010],v[1010],l=0,r=10000,mid,gs,dp[1010],ss[1010],vv[1010];
int check(long long x)
{
	gs=0;
	for(int i=1;i<=n;i++)
	{
		if(s[i]<=x)
		{
			ss[++gs]=s[i];
			vv[gs]=v[i];
		}
	}
	for(int i=1;i<=gs;i++)//背包模板
	{
		for(long long j=S;j>=ss[i];j--)
		{
			dp[j]=max(dp[j],dp[j-ss[i]]+vv[i]);
		}
	}
	if(dp[S]>=V)
	{
		for(int i=0;i<=S;i++)
		{
			dp[i]=0;
		}
		return 1;
	}
	else
	{
		for(int i=0;i<=S;i++)
		{
			dp[i]=0;
		}
		return 0;
	}
}
int main()
{
	scanf("%lld%lld%lld",&n,&V,&S);
	for(int i=1;i<=n;i++)
	{
		scanf("%lld%lld",&s[i],&v[i]);
	}
	for(;l<=r;)//二分答案
	{
		mid=(l+r)/2;
		if(check(mid))//背包判断
		{
			r=mid-1;
		}
		else
		{
			l=mid+1;
		}
	}
	if(l>1000)
	{
		cout<<"No Solution!";//不要忘了
		return 0;
	}
	cout<<l;
}
