		return 0;
	}
	if(x1==0 && x2==0)
	{
		cout<<"x^2";
		return 0;
	}
	if(x1==0 && x2!=0)
	{
		cout<<"(x";
		if(x2>0)
		{
			cout<<"-";
		}
		if(x2<0)
		{
			cout<<"+";
			x2=abs(x2);
		}
		cout<<x2<<")x";
		return 0;
	}
	if(x2==0 && x1!=0)
	{
		cout<<"(x";
		if(x1>0)
		{
			cout<<"-";
		}
		if(x1<0)
		{
			cout<<"+";
			x1=abs(x1);
		}
		cout<<x1<<")x";
		return 0;
	}
	if(x1!=0 && x2!=0)
	{
		cout<<"(x";
		double max_x=max(x1,x2);
		double min_x=min(x1,x2);
		if(min_x>0)
