```
#include<bits/stdc++.h>//万能头

#define M 1000// define 定义数据大小

using namespace std;

int t;//数据组数 
int m,a[M + 5];//如题意 
int all;//所有边的和 
int sum;//all / 4（每边长度） 
int bian[5];//每个边现在多少 
bool flag;//搜完了吗 (标记)

void dfs(int dep){//基本深搜
	if(flag)return ;//如果被标记就返回
	if(dep > m){//如果搜完了就标记并且返回
		flag = true;
		return ;
	}
	for(int i = 1;i <= 4;i ++){//四条边，1到4深搜
		if(sum - bian[i] >= a[dep]){//如果剩余的可以放下这条边
			bian[i] += a[dep];//放下
			dfs(dep + 1);//递归
			bian[i] -= a[dep];//回溯
		}
	}
}

int main(){
	cin>>t;//多组数据
	for(int i = 1;i <= t;i ++){
		all = sum = 0;//初始化（下面一行也是）
		flag = false;
		cin>>m;
		for(int j = 1;j <= m;j ++)cin>>a[j],all += a[j];//按要求输入
		if(all % 4 != 0){// "剪枝" ，如果不是 4 的倍数就直接输出 "no"
			cout<<"no"<<endl;
			continue;
		}
		sum = all / 4;
		dfs(1);//深搜
		if(flag)cout<<"yes"<<endl;//判断是否标记
		else cout<<"no"<<endl;
	}
	return 0;//养成好习惯，你懂滴
}
