    2、搜索中，对于每种情况特殊处理（解释都在代码注释里了）


```
#include <iostream>
#include <cstring>
using namespace std;
int s;
int m;
int n;
bool vis[10001];
int a[10001];
bool flag;
void dfs(int ans,int sum){//sum是目前已经组成了的边数，ans则是目前第sum+1条边组成的情况（即边的长度）
	if(flag){//如果这组数据已经可以组成一个正方形了，自然不用继续搜索
		return;
	}
	if(ans>s){//当前边的长度超过了正方形边长，即这种解法不能组成正方形的一边
		return;
	}
	if(ans==s){
		dfs(0,sum+1);//可以组成正方形的一边，开始搜索下一条边的组成方案
	}
	if(sum==4){//正方形的四条边都好了，标记，退出
		flag=1;
		return;
	}
	for(int i=1;i<=n;i++){
		if(!vis[i]){
			vis[i]=1;
			dfs(ans+a[i],sum);//没什么好解释了，dfs模板
			vis[i]=0;
		}
	}
}
int main(){
	cin>>m;
	while(m--){
		s=0;
		flag=0;
		memset(a,0,sizeof(0));
		cin>>n;
		for(int i=1;i<=n;i++){
			cin>>a[i];
			s+=a[i];
		}
		if(s%4!=0){
			cout<<"no"<<endl;
			continue;
		}
		s/=4;
		dfs(0,0);
		if(flag){
			cout<<"yes"<<endl;
		}
		else{
			cout<<"no"<<endl;
		}
	}
	return 0;
}
