```cpp
#include <iostream>
using namespace std;

//5个参数:上个数、当前需要分配的数、盘子编号(从0开始)、k、方案数 
void DFS(int last, int n, int x, const int k, int &tot) {
    if(x == k) { //已经分了K次 
        if(n == 0) tot ++; //一种方案 
        return;
    }
    for(int i=last; i <= n; i ++) //一定要保证当前数不小于上个数 
        F(i, n-i, x+1, k, tot);
}

int main() {
    int T, n, k, tot;
    cin >> T;
    while(T --) {
        cin >> n >> k;
        DFS(0, n, 0, k, tot = 0); //tot记得赋0 
        cout << tot << endl;
    }
    return 0;
}


