LL up,down,f[20][20][20];
int main()
{
    int t,n,m;
    scanf("%d",&t);
    while(t--)
    {
        memset(f,0,sizeof(f));   //注意清零
        scanf("%d%d",&m,&n);
        f[0][0][0]=1;
        for(int i=0;i<=m;++i)
         for(int j=1;j<=n;++j)
           for(int k=0;k<=i;++k)
                for(int l=0;l<=k;++l)
                 f[i][j][k]+=f[i-k][j-1][l];
