        dp[0][i]=1;
     } */
    for(int i=1;i<=n;++i)
        for(int j=1;j<=k;++j)
        {
            if(i<j)
            {
                //dp[i][j]=dp[i][j-1];
                dp[i][j]=0;
                continue;
            }
            dp[i][j]=max(dp[i-j][j]+dp[i-1][j-1],dp[i][j]);
        }
    int ans=0;
    for(int i=1;i<=k;++i) ans+=dp[n][i]; 
    cout<<ans<<endl;
    }
    return 0;
}
