inline long long read()
{
    char c=getchar(); long long num=0,f=1;
    while (c<'0'||c>'9') { if (c=='-') f=-1; c=getchar(); }
    while (c<='9'&&c>='0') { num=num*10+c-'0'; c=getchar(); }
    return num*f;
}
long long n,ans=0;
int main()
{
    n=read();
    long long m=0;
    for (int i=1;i<=n/5;i++)
    {
        int num=i*5;
        ans+=m*4;
        while (num%5==0) num/=5,m++;
        ans+=m; 
    }
