#include <cmath>
#include <queue>
#include <string>
#include <vector>
#include <cstdio>
#include <cstring>
#include <iostream>
#include <algorithm>
#define space printf(" ")
#define enter printf("\n")
using namespace std;

int n, dp[105][105];
char c[105];
string s[105][105];

long long Read() {
	long long k = 0, tmp = 1; char c = getchar();
	while (c > '9' or c < '0') {
		if (c == '-') tmp = -1;
		c = getchar();
	}
	while (c <= '9' and c >= '0') {
		k = (k << 1) + (k << 3) + (c ^ 48);
		c = getchar();
	}
	return k * tmp;
}
void Write(long long x) {
	if (x < 0) {
		putchar('-');
		x = -x;
	}
	if (x >= 0 and x <= 9) {
		printf("%c", x + '0');
		return;
	}
	Write(x / 10);
	printf("%c", x % 10 + '0');
}
string Change(int x) {// 将压缩数转移成字符串形式
	string now = "";
	while (x) {
		now = (char)(x % 10 + '0') + now;
		x /= 10;
	}
	return now;
}
int Get_Ans(int l, int y);
void Work();

int main() {
	Work();
}

int Get_Ans(int x, int y) {
	int k = y - x + 1, len = y - x + 1, t, w;// k为最终答案，len为当前子串长度
	string tmp = "";
	bool ok;
	for (int l = x; l <= y; l++) {// 首先无法压缩，便为原串
		tmp = tmp + c[l];
	}
	for (int l = 1; l < len; l++) {
		if (len % l != 0) continue;// 无法整除，不能压缩
		ok = true, w = len / l;// w为压缩的次数x
		for (int i = 1; i <= l; i++) {
			for (int j = 2; j <= w; j++) {
				if (c[x + l * (j - 1) + i - 1] != c[x + i - 1]) {// 判断是否能够压缩
					ok = false;
					break;
				}
			}
			if (!ok) break;
		}
		if (ok) {
			t = 0;
			while (w) {
				w /= 10;
				t++;
			}
			w = len / l;
			if (k > t + 2 + dp[x][x + l - 1]) {// 如果能够压缩并不是该段的长度，而是dp[x][x + l - 1]，因为可以再次压缩
				k = t + 2 + dp[x][x + l - 1];
				tmp = Change(w) + '(' + s[x][x + l - 1] + ')';// 记录压缩成的字符串
			}
			else if (k == t + 2 + dp[x][x + l - 1] and tmp < Change(w) + '(' + s[x][x + l - 1] + ')') tmp = Change(w) + '(' + s[x][x + l - 1] + ')';// 如果长度相同，则找字典序大的
		}
	}
	s[x][y] = tmp;// 最后更新压缩串
	return k;
}

void Work() {
	cin >> c + 1;
	n = strlen(c + 1);
	for (int i = 1; i <= n; i++) {
		dp[i][i] = 1;// 初始化，一定记住，要养成好习惯
		s[i][i] = c[i];
	}
	for (int l = 2; l <= n; l++) {
		for (int i = 1, j; i <= n; i++) {
			j = i + l - 1;
			if (j > n) break;// 再大就没这个串了
			dp[i][j] = Get_Ans(i, j);// 动态转移方程中的第二个参数
			for (int k = i; k < j; k++) {// 第一个参数
