void dfs(int x) {
    int i;
    if (temp == n) {//当temp的值达到要拆分的自然数的值的时候 
        print();
        return ;
    }
    for (i = 1; i < n; i++) {
        if (i >= a[x - 1]) {//算式后面的数要比前面的数大或相等 
            a[x] = i;
            temp += a[x];
            if (temp > n) {
            temp -= a[x];//防止temp数过大 
            a[x] = 0;
            break;
            }
            dfs(x + 1);
            temp -= a[x];
            a[x] = 0;    
            //回溯 
        }
    }
}
int main() {
    cin >> n;
    dfs(1);
    return 0;
}
