	rep(i,1,n){
		if(rk[i] == 1) continue;
		if(k) k--;
		int j = sa[rk[i]-1];
		while(i+k <= n && j+k <= n && s[i+k] == s[j+k]) k++;
		height[rk[i]] = k;
	}
}
signed main(){
	n = read();
