void update(int &x, int pos){
    int id, tmp = x; x = ++cnt;
    for(int i=0; i<26; ++i) a[x][i] = a[tmp][i];
    sum[x] = sum[tmp] + 1;
    g[x] = g[tmp];
    if(pos == len){
        g[x].push_back(cur);
        return;
    }
    if(s[cur][pos] >= 'A' && s[cur][pos] <= 'Z') id = s[cur][pos]-'A';
    else id = s[cur][pos]-'a';
    update(a[x][id], pos+1);

}
void solve(int l, int r){
    ans = r;
    for(int i=25; ~i; --i){
        if(sum[a[r][i]] - sum[a[l][i]] > 0){
            solve(a[l][i], a[r][i]);
            return;
        }
    }
}
int main(){
    int n, m, l, r;
    scanf("%d%d",&n,&m);
    for(int i=1; i<=n; ++i){
        scanf("%s",s[i]);
        cur = i;
        len = strlen(s[i]);
        root[i] = root[i-1];
        update(root[i], 0);
    }
    while(m--){
        scanf("%d%d",&l,&r);
        solve(root[l-1], root[r]);
        int x = upper_bound(g[ans].begin(), g[ans].end(), r)-g[ans].begin();
        if(x != 0) --x;
        printf("%s\n",s[g[ans][x]]);
    }
    return 0;
