//把一个字串的全部字符变成小写的 
inline void strlower(string &s){ 
	int sz=s.size();
	for(int i=0;i<sz;i++)
		s[i]=tolower(s[i]);
}

//找出小写处理后的字串那个大 
string strmax(const string &a,const string &b){
	string aa=a,bb=b;
	strlower(aa),strlower(bb);
	if(aa>bb)
		return a;
	else
		return b;
}

```

### 建立线段树
```cpp
void build(int l,int r,int node){
	if(l==r){//如果是叶节点，直接赋值 
		tree[node]=s[l];
		return ;
	}
	int mid=(l+r)>>1;
	int lnode=(node<<1)+1;
	int rnode=(node<<1)+2;
	build(l,mid,lnode);
	build(mid+1,r,rnode);
	//维护线段树的性质，即区间最大字串 
	tree[node]=strmax(tree[lnode],tree[rnode]);
}
```

### 查询

```cpp
string query(int l,int r,int node){
	//如果现在的区间不在查询范围内，就舍弃该区间 
	if(a>r||b<l) 
		return "";//空串是字典序最小的字串 
	if(l==r||(a<=l&&b>=r))
		return tree[node];
	int mid=(l+r)>>1;
	int lnode=(node<<1)+1;
	int rnode=(node<<1)+2;
	//查找最大值 
	string lmax=query(l,mid,lnode);
	string rmax=query(mid+1,r,rnode);
	return strmax(lmax,rmax);
}
