#include<cstdio>
using namespace std;
int exgcd(int a,int b,int &x,int &y)
{
	if(b==0)
	{
		x=1;
		y=0;
		return a;
	}
	int d=exgcd(b,a%b,y,x);
	y-=a/b*x;
	return d;
}
int n;
int c[17],p[17],l[17];
int check(int m)
{
	for(int i=1;i<=n;i++)
	{
		for(int j=i+1;j<=n;j++)
		{
			int A=p[i]-p[j];
			int B=m;
			int C=c[j]-c[i];
			int x,y;
			int ans=exgcd(A,B,x,y); 
			if(C%ans)continue;
			A/=ans;
			B/=ans;
			C/=ans;
			if(B<0)B=-B;
			x=(x*C%B+B)%B;
			if(x<=l[i]&&x<=l[j])return 0;
		}
	}
