#include<cstdio>
#include<cstring>
#include<iostream>
#include<algorithm>
#define _R register
#define int long long 
using namespace std;
int x, y;
long long SolveSegment(const int L,const int R,const int Val)
{
    return (((L + R) * 1LL * (R - L + 1LL)) / 2LL) * 1LL * Val;
}
long long GetIt(int x)
{
    if(x <= 0) return 0LL;
    if(x == 1) return 1LL;
    long long res = 0;
    for(_R int i = 1;i < x;){
        int now = x / i;
        int L = i;
        int R = x;
        int ans;
        if(now == 1){
            res += SolveSegment(ans, x, 1);
            break;
        }
        while(L < R)
        {
            int mid = (L + R) >> 1;
            if((x / mid) < now) ans = mid, R = mid;
            else L = mid + 1;
        }
        res += SolveSegment(i, ans - 1, x / i);
        i = ans;
    }
    return res;
}
signed main()
{
    cin >> x >> y;
    long long ans =  GetIt(y) - GetIt(x - 1);
    printf("%lld",ans);
    return 0;
}
