#include <bits/stdc++.h>
using namespace std;
long long a,b;
int a0[1001],b0[1001];
int a11,b11;
int t;
int main(){
	cin>>a>>b;
	if(a*2<=b) {//判断 2a<=b 的情况
		cout<<floor(log2(b))<<endl;
		return 0;
	}
	a11=a;
	b11=b;
	int len1=0,len2=0;
	while(1){//将二进制表达求出
		a0[++len1]=a%2;
		a/=2;
		if(a==0) break;
	}
	while(1){
		b0[++len2]=b%2;
		b/=2;
		if(b==0) break;
	}
	if(a11==b11){//判断 a==b 的情况
		for(int i=len1; i>=1; i--){
			if(a0[i]==1){
				t++;
			}
		}
		cout<<t<<endl;
		return 0;
	}
	for(int i=len1-1; i>=1; i--){//其他情况
		if(a0[i]==b0[i]&&a0[i]==0&&b0[i]==0){
			t++;
		} else break;
	}
	cout<<len1-t-1<<endl;
	return 0;
}
