    } else if (T == 3) {
    	cout<<3<<endl<<12<<endl<<2<<endl;
    } else if (T == 4) {
    	printf("%.3lf\n",500.0/3.0);
    } else if (T == 5) {
    	cout<<15<<endl;
    } else if (T == 6) {
    	cout<<sqrt(6*6+9*9)<<endl;
    } else if (T == 7) {
    	cout<<110<<endl<<90<<endl<<0<<endl;
    } else if (T == 8) {
    	double const pi=3.141593;
    	double const r=5;
    	cout<<pi*r*2<<endl<<pi*r*r<<endl<<4.0/3*pi*r*r*r<<endl;
    } else if (T == 9) {
    	cout<<22<<endl;
    } else if (T == 10) {
