	int le=qujian[1].l,re=qujian[1].r;
	for(RI i=2;i<=n;i++)
	{
		if(re<qujian[i].l)
		{
			printf("%d %d\n",le,re);
			le=qujian[i].l;
		}
		le=std::min(le,qujian[i].l);
		re=std::max(qujian[i].r,re);
	}
	printf("%d %d\n",le,re);
