#include<bits/stdc++.h>//万能的万能头。 
using namespace std;
int n,m,a[101010],b[101010],c[101010],d[101010],e,f,g,h;//定义变量。 
struct sb{//结构体 
   int x,y;
}q[101010];
bool cmp(sb a,sb b)//sort函数，用来排序。 
{
    if(a.x!=b.x)
    return a.x<b.x;
    return a.y<b.y;
}
int main()
{
    while(scanf("%d",&n)!=EOF)//while循环。 
    {
        f=0;
        scanf("%d",&m);
        for(int i=1;i<=n;i++)
        {
            scanf("%d",&a[i]);//输入。
        }
        for(int i=1;i<=m;i++)
        {
            scanf("%d",&b[i]);//输入。
        }
        e=(n+m)*10+1;
        for(int i=2;i<=e;i++)
        {
            int g=0,h=101010;//重置变量g,给h值P.S,h的值一定要大于五位数，（我血的教训.... 
            for(int j=1;j<=n;j++)
            {
                c[j]=a[j]%i;
                if(c[j]==0)
                {
                g=max(g,i);	
				}
                else
                {
                	g=max(c[j],g);
				}
            }
            for(int j=1;j<=m;j++)
            {
                d[j]=b[j]%i;
                if(d[j]==0)
                {
                	h=min(h,i);
				}
                else
                {
                	h=min(h,d[j]);
				}
            }
            if(g<h)
            {
                q[++f].x=g;
                q[f].y=i-g;
            }
        }
        if(!f)//如果不是，输出NO。 
        {
            cout<<"NO"<<"\n";//"\n"跳下一行（当年天真的我用了endl，结果超时了........ 
            continue;
        }
        sort(q+1,q+f+1,cmp);//排序。 

        cout<<q[1].x<<" "<<q[1].y<<"\n";//用这个也是可以的printf("%d %d\n",q[1].x,q[1].y);本人测试过了 
    }
	return 0;//结束。P.S,return 0不能放在循环里，不然......又是血的教训........
