inline bool solve(int x)
{
    int now;
    bool flag = 0;
    for(register int i = 1; i <= m; ++i)
    {
        if(vis[i]) continue;
        if(re[i].a[x] != 0)
        {
            flag = 1;
            vis[i] = 1;
            an = max(an, i);
            now = i;
            break;
        }
