#define ll long long
#define db double
#define inf 0x7fffffff

using namespace std;

bitset<1005> s[2001];
int n,m,ans,now=1;

inline int qr(){//快读
	char ch;
	while((ch=getchar())<'0'||ch>'9');
	int res=ch^48;
	while((ch=getchar())>='0'&&ch<='9')
		res=(res<<1)+(res<<3)+(ch^48);
	return res;
}

inline int rd(){
	char ch;
	while((ch=getchar())<'0'||ch>'9');
	return ch^48;//每次只读一个
}

int main(){
	//freopen(".in","r",stdin);
	//freopen(".out","w",stdout);
	n=qr()+1,m=qr();
	for(int i=1;i<=m;++i)//读入
		for(int j=1;j<=n;++j)
			s[i][j]=rd();
	for(int i=1;i<n;now=++i){
		while(!s[now][i]&&now<=m)++now;
		ans=max(ans,now);//更新 k 值
		if(now==m+1){
			printf("Cannot Determine\n");
			return 0;
		} //方程不构成唯一解
		if(now!=i)swap(s[i],s[now]);
		for(int j=1;j<=m;j++){
			if(i==j)continue; //不消自己
			if(!s[j][i])continue;//不是1就不用消
			s[j]^=s[i];//用异或消去系数 1
		}//消去其他方程的系数
	}
	printf("%d\n",ans);
	for(int i=1;i<n;++i)
		if(s[i][n])printf("?y7M#\n");
