        if(r==m+1) {ans=0;return;}
        ans=max(ans,r);
        if (r!=i) for (int j=1;j<=n+1;j++) swap(a[i][j],a[r][j]);
        for (int j=1;j<=m;j++) if (j!=i && a[j][i]) {
