```cpp
#include<cstdio>
#include<bitset>
using namespace std;
const int N=1800;
void read(int &x)
{
    x=0;
    int f;
    f=1;
    char c;
    c=getchar();
    while((c<'0'||c>'9')&&c!='-')
    {
        c=getchar();
    }
    if(c=='-')
    {
        f=-1;
        c=getchar();
    }
    while(c>='0'&&c<='9')
    {
        x=(x<<3)+(x<<1)+(c^48);
        c=getchar();
    }
    x=x*f;
}
int a[N];
struct node
{
    int next;
    int to;
};
node e[N];
int head[N],etot,dp[N][3];// 0 fa 1 self 2 son
bitset<N> book;
void add(int x,int y)
{
    etot++;
    e[etot].to=y;
    e[etot].next=head[x];
    head[x]=etot;
}
inline int min(int x1,int x2)
{
    return x1<x2 ? x1 : x2;
}
inline int max(int x1,int x2)
{
    return x1>x2 ? x1 : x2;
}
void dfs(int x)
{
    int i,now;
    now=0x3f3f3f3f;
    for(i=head[x];i!=0;i=e[i].next)
    {
        dfs(e[i].to);
        dp[x][0]+=min(dp[e[i].to][1],dp[e[i].to][2]);
        dp[x][1]+=min(dp[e[i].to][1],min(dp[e[i].to][2],dp[e[i].to][0]));
    }
    dp[x][2]+=dp[x][0];
    for(i=head[x];i!=0;i=e[i].next)
    {
        now=min(now,dp[e[i].to][1]-dp[e[i].to][2]);
    }
    dp[x][2]+=max(now,0);
}
int main()
{
    etot=0;
    int n,i,m,x,j;
    read(n);
    for(i=1;i<=n;i++)
    {
        read(i);
        read(a[i]);
        dp[i][1]=a[i];
        read(m);
        for(j=1;j<=m;j++)
        {
            read(x);
            book[x]=1;
            add(i,x);
        }
    }
    for(i=1;i<=n;i++)
    {
        if(book[i]==0)
        {
            dfs(i);
            printf("%d",min(dp[i][1],dp[i][2]));
            return 0;
        }
    }
}
