#include<algorithm>
#include<cstdio>
using namespace std;
const int MAXN = 1510; 
const int MAXM = 3010; 
int n, m; 
#define LL long long
int A[MAXN]; 
int G[MAXN][MAXN]; 
int ind[MAXN]; 
LL F[MAXN][3]; 
// 0 - 没有保安且不受孩子控制  1 - 没有保安但受子节点控制  2 - 有保安 
void search(int x)
{
　　int y, e; 
　　LL sum0, sum1, sum2; 
　　sum0 = sum1 = sum2 = 0; 
　　for(e = 1; e <= G[x][0]; ++e)
　　{
　　　　y = G[x][e]; 
　　　　search(y); 
　　　　sum0 += F[y][1]; // 不同之处 
　　　　sum1 += min(F[y][1], F[y][2]); 
　　　　sum2 += min(F[y][0], min(F[y][1], F[y][2])); 
　　}
　　for(e = 1; e <= G[x][0]; ++e)
　　{
　　　　y = G[x][e]; 
　　　　F[x][1] = min(F[x][1], sum1 - min(F[y][1], F[y][2]) + F[y][2]); 
　　}
　　F[x][0] = sum0; 
　　F[x][2] = sum2 + A[x]; 
}
int main()
{
　　int i, x, y, k; 
　　scanf("%d", &n); 
　　for(i = 1; i <= n; ++i)
　　{
　　　　F[i][0] = F[i][1] = F[i][2] = 0x7f7f7f7f; 
　　}
　　for(i = 1; i <= n; ++i)
　　{
