　　　　scanf("%d", &k); 
　　　　for(; k--; )
　　　　{
　　　　　　scanf("%d", &y); 
　　　　　　G[x][++G[x][0]] = y; 
　　　　　　++ind[y]; 
　　　　}
　　}
　　LL ans = 0x7f7f7f7f; 
　　for(y = 1; y <= n; ++y) 
　　{
　　　　if(!ind[y])
　　　　{
　　　　　　search(y); 
　　　　　　ans = min(ans, min(F[y][1], F[y][2])); 
　　　　}
　　}
　　printf("%lld\n", ans); 
　　return 0; 
}


