#include<bits/stdc++.h>
#define mod p
#define K 20
#define int long long
using namespace std;
int n,m,p;

struct mat//封装结构体
{
    int m[K][K];//矩阵
    int* operator[](int i)//封装取下标运算符
    {
        return m[i];
    }

    mat(int x)//初始化矩阵全部为x
    {
        for(int i=0;i<K;i++)
        {
            for(int j=0;j<K;j++)
            {
                m[i][j]=x;
            }
        }
    }
    void Set(int x=0)//方便初始化之后全部设置为x（这个里面没有用到）
    {
        for(int i=0;i<K;i++)
        {
            for(int j=0;j<K;j++)
            {
                m[i][j]=x;
            }
        }
    }
    void Identity()//单位矩阵
    {
        Set(0);
        for(int i=0;i<K;i++)
        {
            m[i][i]=1;
        }
    }
};
mat operator*(mat x,mat y)//封装乘法
{
    mat ans(0);

    for(int i=1;i<K;i++)
    {
        for(int j=1;j<K;j++)
        {
            ans[i][j]=0;
            for(int k=1;k<K;k++)
            {
                ans[i][j]+=x[i][k]*y[k][j];
                ans[i][j]%=mod;//取模。。。
            }
        }
    }
    return ans;
}
mat operator^(mat x,int p)//封装快速幂
{
    mat ans(0);ans.Identity();//初始为单位矩阵
    while(p)
    {
        if (p&1) ans=ans*x;
        x=x*x,p>>=1;
    }//和快速幂差不多
    return ans;
}

int k;
int b[K],c[K];
int s[K];
void Input()
{
    scanf("%lld",&k);
    for(int i=1;i<=k;i++)
    {
        scanf("%lld",&b[i]);
    }
    for(int i=1;i<=k;i++)
    {
        scanf("%lld",&c[i]);
    }
    scanf("%lld%lld%lld",&m,&n,&p);

    for(int i=1;i<=k;i++)
    {
        s[i]=(s[i-1]+b[i])%mod;//处理前缀和
        b[i]%=mod;
        c[i]%=mod;
    }
}

int Calc(int x)
{
    if (x<=k)//这个很显然
    {
        return s[x];
    }
    else
    {
        mat Initial(0);//Initial矩阵
        for(int i=1;i<=k;i++) Initial[1][i]=b[i];
        Initial[1][k+1]=s[k];

        mat Trans(0);//Trans矩阵
        for(int i=1;i<=k;i++)
        {
            Trans[i][k]=Trans[i][k+1]=c[k-i+1];
        }
        for(int i=2;i<=k;i++)
        {
            Trans[i][i-1]=1;
        }
        Trans[k+1][k+1]=1;//构造方法见上面

        mat ans=Initial*(Trans^(x-k));
        return ans[1][k+1];
    }
}

