#define N 42005 
using namespace std; 
typedef long long LL; 
LL ans; 
int n,p; 
LL f[2][N]; 
int main() 
{ 
    scanf("%d%d",&n,&p); 
    f[1][1]=1; 
    bool q; 
    for(int i=2;i<=n;++i) 
    { 
        q=i&1; 
        for(int j=1;j<=i;++j) 
            f[q][j]=LL(f[q][j-1]+f[!q][i-j+1])%p; 
    } 
    for(int i=1;i<=n;++i) 
        ans=LL(ans+f[q][i])%p; 
    printf("%lld",(ans<<1)%p); 
    return 0;  
} 
