#include <cstdio>
int sc[20] , need[20];
double f[102][32770];
double max(double a , double b)
{
    return a > b ? a : b;
}
int main()
{
    int k , n , i , j , l , x;
    scanf("%d%d" , &k , &n);
    for(i = 1 ; i <= n ; i ++ )
    {
        scanf("%d" , &sc[i]);
