    for(i = k ; i ; i -- )
    {
        for(j = 0 ; j < 1 << n ; j ++ )
        {
            for(l = 1 ; l <= n ; l ++ )
                if(!((~j) & need[l]))
                    f[i][j] += max(f[i + 1][j] , f[i + 1][j | (1 << (l - 1))] + sc[l]);
