#include <bits/stdc++.h>
using namespace std;

#define int long long

const int INF = 0x7f7f7f7f;

int n,a,b,g[55][55],c1,c2,c3;

void floyd()
{
	for(int k=0;k<=n;k++)
	for(int i=0;i<=n;i++)
	for(int j=0;j<=n;j++)
		if(g[i][k]<INF && g[k][j]<INF)
			g[i][j] = min(g[i][j],g[i][k]+g[k][j]);
}

bool legal(int x,int wx,int y,int wy)
{
	return wx-wy<=g[y][x] && wy-wx<=g[x][y];
}

signed main()
{
	memset(g,INF,sizeof(g));
	cin>>n>>a>>b;
	for(int i=0;i<=n;i++)
		g[i][i] = 0;
	for(int i=1;i<=n;i++)
		g[i][0] = -1, g[0][i] = 3;
	for(int i=1;i<=n;i++)
		for(int j=1;j<=n;j++)
		{
			char ch;
			cin>>ch;
			if(ch=='=')
				g[i][j] = min(g[i][j],0ll), g[j][i] = min(g[j][i],0ll);
			else if(ch=='+')
				g[i][j] = min(g[i][j],-1ll);
			else if(ch=='-')
				g[j][i] = min(g[j][i],-1ll);
		}
	floyd();
	for(int i=1;i<=n;i++)
	for(int j=i+1;j<=n;j++)
	{
		if(i==a || i==b || j==a || j==b)
			continue;
		int f1=0,f2=0,f3=0;
		for(int wa=1;wa<=3;wa++)
		for(int wb=1;wb<=3;wb++)
		for(int wi=1;wi<=3;wi++)
		for(int wj=1;wj<=3;wj++)
		{
		if(legal(i,wi,j,wj) && legal(i,wi,a,wa) && legal(i,wi,b,wb) && legal(j,wj,a,wa) && legal(j,wj,b,wb) && legal(a,wa,b,wb))
			{
