	for(int u = 1; u <= n; u ++){
		for(int v = 1; v <= n; v ++){
			char c; cin >> c;
			if(c == '+') Max[u][v] = 2, Min[u][v] = 1;
			if(c == '-') Max[u][v] = - 1, Min[u][v] = - 2;
			if(c == '=') Max[u][v] = 0, Min[u][v] = 0;
