        if((x[e][1]>=0)&&(x[x[e][1]][1]<0)) e=x[e][1];
        x[i][4]=e;
        if(e==c) c=x[e][1];
        else
        {
            x[x[e][3]][1]=x[e][1];
            if(x[e][1]>=0) x[x[e][1]][3]=x[e][3];
