function mo(x,y:longint):longint;      //防止越界
var t:int64;
begin
  t:=x;
  t:=t*y;
  t:=t mod maxn;
  mo:=t;
end;
function work(a1,a2,a3,a4,a5,m:longint):longint;         //递归
