#include<bits/stdc++.h>
#define ll long long
using namespace std;
map <ll,ll> m;
ll QuickPow(ll x,ll y,ll p)
{
	ll sum=1;
	while(y)
	{
		if(y&1) sum=sum*x%p;
		x=x*x%p;
		y>>=1;
	}
	return sum%p;
}
ll Gcd(ll x,ll y)
{
	return !x ? y : Gcd(y%x,x);
}
ll ExGCD(ll a,ll b,ll &x,ll &y)
{
	if(!b)
	{
		x=1;
		y=0;
		return a;
	}
	ll g=ExGCD(b,a%b,x,y);
	ll t=x;
	x=y;
	y=t-y*(a/b);
	return g;
}
ll BSGS(ll a,ll b,ll p,ll d)
{
	m.clear();
	b%=p;
	ll t=sqrt(p)+1,now=b,sum=d;
	for(int i=1;i<=t;i++)
	{
		now=now*a%p;
		m[now]=i;
	}
	now=QuickPow(a,t,p);
	for(int i=1;i<=t;i++)
	{
		sum=sum*now%p;
		if(m.count(sum)) return t*i-m[sum];
	}
	return -1;
}
int ExBSGS(int a,int b,int p)
{
	if(p==1) return 0;
	a%=p;
	b%=p;
	if(b==1) return 0;
	if(a==0)
	{
		if(b==0) return 1;
		return -1;
	}
	ll sum=1,tot=0;
	for(ll i=Gcd(a,p);i!=1;i=Gcd(a,p))
	{
		if(b%i) return -1;
		b/=i;
		p/=i;
		sum=sum*(a/i)%p;
		tot++;
		if(b==sum) return tot;
	}
	a%=p;
	ll cnt=BSGS(a,b,p,sum);
	if(cnt==-1) return -1;
	else return cnt+tot;
}
