```cpp
#include <bits/stdc++.h>
#define ll long long
#define int ll
std::unordered_map<int, int> h;
int gcd(int a, int b) { return b ? gcd(b, a % b) : a; }
inline int mul(int a, int b, int p)
{
    ll ret = 0;
    while(b)
    {
        if (b & 1)
            ret = (ret + a) % p;
        a = (a + a) % p;
        b >>= 1;
    }
    return ret;
}
void exgcd(int a, int b, int &x, int &y)
{
    if (b == 0)
    {
        x = 1, y = 0;
        return;
    }
    exgcd(b, a % b, x, y);
    int t = x;
    x = y, y = t - (a / b) * y;
}
int solve1(int a, int b, int p)
{
    ll ret = 1;
    while (b)
    {
        if (b & 1)
            ret = mul(ret, a, p);
        a = mul(a, a, p);
        b >>= 1;
    }
    return ret;
}

int solve2(int a, int b, int p)
{
    int x = 0, y = 0;
    int g = gcd(a, p);
    if (b % g)
        return -1;
    exgcd(a, p, x, y);
    x *= (b / g);
    x = (x % p + p) % p;
    return x;
}
int solve3(int a, int b, int p)
{
    if (b == 1)
        return 0;
    int cnt = 0, d, k = 1;
    while ((d = gcd(a, p)) ^ 1)
    {
        if (b % d)
            return -1;
        b /= d, p /= d, ++cnt;
        k = mul(k, a / d, p);
        if (k == b)
            return cnt;
    }
    int t = sqrt(p) + 1, tmp = 1;
    h.clear();
    for (int i = 0; i < t; i++)
    {
        h[mul(tmp, b, p)] = i;
        tmp = mul(tmp, a, p);
    }
    k = mul(k, tmp, p);
    for (int i = 1; i <= t; i++)
    {
        if (h.find(k) != h.end())
            return i * t - h[k] + cnt;
        k = mul(k, tmp, p);
    }
    return -1;
}
signed main()
{
