	return x<y?x:y;
}
bool S1;
coint M=30+3;
coint dx[]={1,0,-1,0},dy[]={0,1,0,-1};
const float eps=1e-9;
#define In(x,y) ((x)&(1<<((y)-'A')))

int n,m,k,hp,S;
int sx,sy;
char mp[M][M];
float p[M];
float totp[M][M];
struct coor{
	short x,y;
};
vector<coor>vec[M][M][M];
int sz[M][M][M];

namespace Init_p{
	int sta[M];
	inline void solve(){
		float tot=0.0;
		repp(i,0,S) tot+=p[i];
		repp(i,0,S) p[i]/=tot;
		repp(i,0,S){
			repp(j,0,S){
				if((i&j)!=j) continue;
				repp(o,0,k){
					if(!In(i,o+'A')) sta[o]=0;
					else if(In(j,o+'A')) sta[o]=1;
					else sta[o]=-1;
				}
				repp(STA,0,S){
					bool flag=1;
					repp(o,0,k){
						if(sta[o]==1 && !In(STA,o+'A')) { flag=0; break; }
						if(sta[o]==-1 && In(STA,o+'A')) { flag=0; break; }
					}
					if(flag) totp[i][j]+=p[STA];
				}
			}
		}
