		if(mark[x][y]) return;
		mark[x][y]=1;
		if(mp[x][y]=='@' || (mp[x][y]>='A' && mp[x][y]<='Z' && (x!=sx || y!=sy) && In(sta,mp[x][y]))) { push_in(x,y,sta,sx,sy); return; }
		for(int i=0; i<4; i++){
			coint xx=dx[i]+x,yy=dy[i]+y;
			if(xx<1 || xx>n || yy<1 || yy>m || mp[xx][yy]=='#') continue;
			dfs(xx,yy,sta,sx,sy);
		}
