short dp[1005][N];
bool judge(int mid)
{
    int ans=0,now=0;
    for(int i=1;i<=n;i++)
    {
        if(a[i]>mid) return false;
        if(now+a[i]<=mid) now+=a[i];
        else now=a[i],ans++;
    }
