    {
        int mid=l+r>>1;
        if(judge(mid)) ans=mid,r=mid-1;
        else l=mid+1;
    }
    for(int i=1,now=0;i<=n;i++)
    {
        while(sum[i]-sum[now]>ans) now++;
        pre[i]=now-1;
        //pre[i]=lower_bound(sum,sum+1+n,sum[i]-ans)-sum-1;
