const ll mode=10000;
ll dp[1005][1005];
ll s[1005][1005];
ll n,k;
int main()
{
	scanf("%lld%lld",&n,&k);
	dp[1][0]=1;
	for(int i=0;i<=k;i++)s[1][i]=1;
	for(int i=2;i<=n;i++)
	{
		for(int j=0;j<=min(n*(n-1)/2,k);j++)
		{
			if(j-i<0)dp[i][j]=s[i-1][j];
			else dp[i][j]=(s[i-1][j]-s[i-1][j-i]+mode)%mode;
		}
		s[i][0]=dp[i][0];
		for(int j=1;j<=k;j++)s[i][j]=(s[i][j-1]+dp[i][j])%mode;
	}
	printf("%lld\n",dp[n][k]);
	return 0;
}
