由于组合数使用线性筛逆元，而本题模数非质数所以估计不能AC，但是对于更大的数据范围仍然有效的lojAC代码：

```cpp
#include <cstdio>
#include <cmath>
#include <cstring>
#include <cstdlib>
#include <iostream>
#include <algorithm>
#include <queue>
#include <stack>
#define ll long long
using namespace std;
const ll mode=1000000007;
const int lim=450;
ll inv[200005];
ll minv[200005];
ll mul[200005];
ll dp[455][100005];
ll n,k;
void init()
{
	inv[0]=inv[1]=mul[0]=mul[1]=minv[0]=minv[1]=1;
	for(int i=2;i<=200000;i++)
	{
		inv[i]=(mode-mode/i)*inv[mode%i]%mode;
		minv[i]=minv[i-1]*inv[i]%mode;
		mul[i]=mul[i-1]*i%mode;
	}
}
ll C(ll x,ll y)
{
	if(x<y)return 0;
	return mul[x]*minv[y]%mode*minv[x-y]%mode;
}
int main()
{
	init();
	scanf("%lld%lld",&n,&k);
	dp[0][0]=1;
	for(int i=1;i<=lim;i++)
	{
		for(int j=0;j<=k;j++)
		{
			if(j>=i)dp[i][j]=(dp[i][j-i]+dp[i-1][j-i])%mode;
			if(j>=n+1)dp[i][j]=(dp[i][j]+mode-dp[i-1][j-n-1])%mode;
		}
	}
	ll ans=0;
	for(int i=0;i<=k;i++)
	{
		ll temps=0;
		ll f=1;
		for(int j=0;j<=lim;j++)temps=(temps+f*dp[j][i]+mode)%mode,f=-f;
		temps=temps*C(n+k-i-1,n-1)%mode;
		ans=(ans+temps)%mode;
	}
	printf("%lld\n",ans);
	return 0;
}
```

