#define rep(i,s,n) for(int (i)=(s);(i)<=(n);++(i))
using namespace std;
const int maxn=1e3+7,mod=1e4;
static int dp[maxn][maxn],n,m,sum;
int main()
{
     scanf("%d%d",&n,&m);
     dp[1][0]=1;
     rep(i,2,n)
  {
      sum=0;
      rep(j,0,m)
      {
          (sum+=dp[i-1][j])%=mod;
         dp[i][j]=sum%mod;
         if(j-i+1>=0)((sum-=dp[i-1][j-i+1])+=mod)%mod;
       }
     }
  printf("%d",dp[n][m]);
  return 0;
}//by fjlyyz-ljy
