                if(f[i-1][j]>f[i][j-1]) g[i][j]=g[i-1][j]%mod;
                else if(f[i-1][j]<f[i][j-1]) g[i][j]=g[i][j-1]%mod;
                else if(f[i][j]!=f[i-1][j-1]) g[i][j]=(g[i-1][j]+g[i][j-1])%mod;
                else g[i][j]=(g[i][j-1]+g[i-1][j]-g[i-1][j-1])%mod;
            }
        }
    }
    writeln(f[lena][lenb]);
    write(g[lena][lenb]);
}
