#include <cstdio>
#include <cstring>
#include <vector>
#include <iostream>
#define getmax(a,b) (((a)>(b))?(a):(b))
using namespace std;
const int maxn=5005,mo=100000000;
int n,m,f[2][maxn],g[2][maxn];
char a[maxn],b[maxn];
