        if(dis[v]==dis[u]-w&&a[i].cap&&!vis[v]){
            if(!(f=dfs(v,t,_min(limit,a[i].cap)))) continue;
            a[i].cap-=f;a[i^1].cap+=f;
            ans+=f*w;flow+=f;limit-=f;
            if(!limit) return flow;
        }
    }
    return flow;
}
int zkw(int s,int t){
