具体思路很简单，建一个源点表示进货处，一个汇点表示卖出，在相邻每天之间建边表示存储货物，在跑一边费用流就可以了。

代码：

```cpp
#include<cstdio>
#include<cstdlib>
#include<cstring>
#include<queue>
#include<iostream>
#define For(i,a,b) for(i=(a);i<=(b);++i)
#define Forward(i,a,b) for(i=(a);i>=(b);--i)
using namespace std;
const int MAXN=40001;
const int inf=0xFFFFFFF;
template<typename T>
inline void read(T &x)//快读
{
    T s=0,f=1;
    char k=getchar();
    while(!isdigit(k)&&k!='-')k=getchar();
    if(!isdigit(k))
    {
        f=-1;
        k=getchar();
    }
    while(isdigit(k))
    {
        s=(s<<3)+(s<<1)+(k^48);
        k=getchar();
    }
    x=f*s;
}
struct edge
{
    int v,w,c,next;
}p[MAXN<<2]; //边
int n,m,s,w[MAXN],e=1,head[MAXN],fee;//各种乱七八糟的东西。只要注意fee表示费用。
void add(int u,int v,int f,int w)//加边。注意边从2开始，至于为什么去看网络流
{
    p[++e].v=v;
    p[e].w=w;
    p[e].c=f;
    p[e].next=head[u];
    head[u]=e;
}
queue<int>G;//队列
int dis[MAXN];//层次
bool vis[MAXN];//标记是否访问
bool spfa(int s,int t)//spfa找边
{
    fill(dis+1,dis+n+3,inf);
    dis[s]=0;
    vis[s]=true;
    G.push(s);
    while(!G.empty())
    {
        int u=G.front(),v=head[u];
        G.pop();
        vis[u]=false;
        while(v)
        {
            if(p[v].c&&dis[p[v].v]>dis[u]+p[v].w)
            {
                dis[p[v].v]=dis[u]+p[v].w;
                if(!vis[p[v].v])
                {
                    vis[p[v].v]=true;
                    G.push(p[v].v);
                }
            }
            v=p[v].next;
        }
    }
    //int i;
    ///For(i,1,n+2)cout<<i<<"           "<<dis[i]<<endl;
    //putchar('\n');
    //system("pause");调试
    return dis[t]!=inf;
}
int dfs(int u,int t,int flow)//找可行流
{
    if(u==t||!flow)return flow;
    vis[u]=true;
    int sum=0,v=head[u];
    while(v)
    {
        if(dis[p[v].v]==dis[u]+p[v].w&&p[v].c&&!vis[p[v].v])//注意Dinic的优化
        {
            int f=dfs(p[v].v,t,min(flow,p[v].c));
            flow-=f;
            p[v].c-=f;
            p[v^1].c+=f;
            fee+=p[v].w*f;
            sum+=f;
        }
        v=p[v].next;
    }
    vis[u]=false;
    return sum;
}
int Dinic(int s,int t)//Dinic
{
    int ans=0;
    while(spfa(s,t))ans+=dfs(s,t,inf);
    return ans;
}
int main()
{
    read(n);
    read(m);
    read(s);
    int i,u,v;
    For(i,1,n)read(w[i]);
    For(i,1,n)
    {
        read(u);
        add(1,i+1,inf,u);//进货处向每天连边(inf,d)
        add(i+1,1,0,-u);
        add(i+1,n+2,w[i],0);//每天向卖出连边(u,0)
        add(n+2,i+1,0,0);
        if(i!=n)
        {
            add(i+1,i+2,s,m);//除最后一天，每天向后一天连边(s,m)
            add(i+2,i+1,0,-m);
        }
    }
    Dinic(1,n+2);
    printf("%d\n",fee);//注意是要求费用
    return 0;
}

