int n, m, MS, S, T, tot, ans;
int head[MAXN], next[MAXN], from[MAXN], to[MAXN];
int pre[MAXN], pas[MAXN], w[MAXN];
void add(int x, int y, int f, int z)
{
    next[++tot] = head[x];
    head[x] = tot;
    to[tot] = y;
    from[tot] = x;
    pas[tot] = f;
    w[tot] = z;

    next[++tot] = head[y];
    head[y] = tot;
    to[tot] = x;
    from[tot] = y;
    pas[tot] = 0;
    w[tot] = -z;
}
int dis[MAXN], flag[MAXN], que[MAXN];
int SPFA()
{
    for(int i = S; i <= T; i++)
    dis[i] = INF;
    dis[S] = 0; flag[S] = 1;
    int t = 0, W = 1;
    que[1] = S;
    while(t < W)
    {
    //    cout<<t<<" "<<W<<endl;
        int u = que[++t];
        for(int i = head[u]; i; i = next[i])
        {
            int v = to[i];
            if(pas[i] && dis[v] > dis[u] + w[i])
            {
                dis[v] = dis[u] + w[i];
                pre[v] = i;
                if(!flag[v])
                {
                    flag[v] = 1;
                    que[++W] = v;
                }
            }
        }
        flag[u] = 0;
    }
//    cout<<dis[T];
    return dis[T] != INF; 
}
void Work()
{
    int x = INF;
    for(int i = pre[T]; i; i = pre[from[i]])
    x = min(x, pas[i]);
    for(int i = pre[T]; i; i = pre[from[i]])
    {
        pas[i] -= x;
        pas[((i - 1) ^ 1) + 1] += x;
        ans += x * w[i];
    }
}
int main()
{
    n = get(); m = get(); MS = get();
    S = 0, T = n + 1;
    for(int i = 1; i <= n; i++)
    add(i, T, get(), 0);
    for(int i = 1; i <= n; i++)
    add(S, i, INF, get());
    for(int i = 1; i < n; i++)
    add(i, i + 1, MS, m);
    while(SPFA())
    Work();
    put(ans);
}
