	for (int i = 1; i <= 50; i++) frac[i] = ksc(frac[i-1],i), invfrac[i] = ksm(frac[i],phimod-1);
	for (int i = 0; i < s.size(); i++) {
		for (int j = 0; j < s[i]-'0'; j++) {
			ull now = frac[s.size()-i-1];
			for (int k = 0; k <= 9; k++) now = ksc(now, invfrac[num[k]-(k==j)]);
			ans += now;
		}
		--num[s[i]-'0'];
	}
	cout << ans << endl;
}
