#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
const int N = 55;
ll cnt[10];
ll ans ;
char s[N] ;
ll C[N][N] ;
int main() {
    scanf("%s", s + 1) ;
    int n = strlen(s + 1);
    for(int i = 1; i <= n; i++) cnt[s[i] - '0']++;
    C[0][0] = C[1][0] = C[1][1] = 1;
    for(int i = 2; i < N; i++) {
        C[i][0] = 1;
        for(int j = 1; j < N; j++)
            C[i][j] = C[i - 1][j - 1] + C[i - 1][j] ;
    }
    for(int i = 1; i <= n; i++) {
        for(int j = 0; j < s[i] - '0'; j++) {
            if(cnt[j] >= 1) {
                ll res = 1;
                cnt[j]--;
                ll sum = n - i;
                for(int k = 0; k <= 9; k++) res *= C[sum][sum - cnt[k]], sum -= cnt[k] ;
                cnt[j]++;
                ans += res;
            }
        }
        cnt[s[i] - '0']--;
    }
    cout << ans;
    return 0;
}

